settings.custom_liquid:
{% if collection.title contains 'AGENTS PORTAL' %}

<div class="agent-menu">
<div class="page-width">
  <figure>
    {{ collection.image | image_url: width: 400 | image_tag: class: 'logo' }}
  </figure>

  <nav class="agent-nav">
    {{ collection.title | remove: ' | AGENTS PORTAL' | append: ' | Home' | link_to: collection.url }}

    {{ 'Flagmakers Home' | link_to: shop.url }}
  </nav>
</div>
</div>


<style>
.header-wrapper{
  display:none;
}
.agent-menu{
  background-color: #70a034;
}
.agent-menu .page-width{
  display: flex;
  gap: 1rem;
  justify-content: space-between;
  align-items: center;
}
.agent-menu .logo{
  height: 100px;
  width: 150px;
  object-fit: contain;
  object-position: left center;
}
.agent-menu nav{
  display: flex;
  flex-wrap: wrap;
  gap: 1rem;
  flex-direction: column;
  align-items: flex-end;
}
</style>
{% endif %}
