settings.custom_liquid:
{% if collection.title contains 'AGENTS PORTAL' %}

{% assign bgcolour = collection.metafields.custom.header_colour | default: '#70a034' %}

<div class="agent-menu">
<div class="page-width">
    {{ collection.image | image_url: width: 400 | image_tag: class: 'logo' }}

  <nav class="agent-nav">
    {{ collection.title | remove: ' | AGENTS PORTAL' | append: ' | Home' | link_to: collection.url }}

    {{ 'Flagmakers Home' | link_to: shop.url }}

  <a class="cart">
{%- liquid
  if cart == empty
    render 'icon-cart-empty'
  else
    render 'icon-cart'
  endif
-%}
<span class="visually-hidden">{{ 'templates.cart.cart' | t }}</span>
{%- if cart != empty -%}
  <div class="cart-count-bubble">
    {%- if cart.item_count < 100 -%}
      <span aria-hidden="true">{{ cart.item_count }}</span>
    {%- endif -%}
    <span class="visually-hidden">{{ 'sections.header.cart_count' | t: count: cart.item_count }}</span>
  </div>
{%- endif -%}
</a>
  </nav>
</div>
</div>


<style>
.header-wrapper,
.shopify-section[id*="__breadcrumbs"],
.shopify-section[id*="__related-products"]{
  display:none;
}
.agent-menu{
  padding: 1rem 0;
  background-color: {{ bgcolour }};
}
.agent-menu .page-width{
  display: flex;
  gap: 1rem;
  justify-content: space-between;
  align-items: center;
}
.agent-menu .logo{
  height: 100px;
  width: 150px;
  object-fit: contain;
  object-position: left center;
}
.agent-menu nav{
  display: flex;
  flex-wrap: wrap;
  gap: 1rem;
  flex-direction: column;
  align-items: flex-end;
  text-align: right;
}
.agent-menu nav a{
  color: #fff;
  opacity: 0.75;
}
.agent-menu nav a:hover{
  opacity: 1;
}
.agent-menu nav cart{
  position: relative;
}

.collection-hero{
  background-color: {{ bgcolour }};
  padding-bottom: 4rem !important;
}
.collection-hero h1,
.collection-hero .collection-hero__description{
color: #fff;
}
</style>
{% endif %}
